import React, { useEffect, useState } from "react";
import { animateScroll } from "react-scroll";
import { AboutSide, BackToTop, LogoCard } from "../../images/image";
import ScrollToNextSection from "../ScrollToNextSection/ScrollToNextSection";
import SideImage from "../SideImage/SideImage";
import styles from "./styles.module.css";

const About = () => {
  const [hideDetails, setHideDetails] = useState(false);
  const [hideImage, setHideImage] = useState(false);

  useEffect(() => {
    let prevScrollpos = window.pageYOffset;
    window.addEventListener("scroll", () => {
      const currentScrollPos = window.pageYOffset;
      if (prevScrollpos > currentScrollPos) {
        setHideDetails(false);
        setHideImage(false);
      } else {
        setHideDetails(true);
        setHideImage(true);
      }
      prevScrollpos = currentScrollPos;
    });
  }, []);

  return (
    <section className={styles.wrapper} id="about">
      <div className={`${styles.details} ${hideDetails ? styles.hide : ""}`}>
        <div className={styles.logoWrapper}>
          <img src={LogoCard} alt="Logo" className={styles.logo} />
        </div>
      </div>
      <SideImage
        image={AboutSide}
        className={`${styles.sideImage} ${hideImage ? styles.hideImage : ""}`}
      />
      <ScrollToNextSection nextSection="testimonial" />
      <img
        src={BackToTop}
        onClick={() => animateScroll.scrollToTop()}
        alt="#"
        className={styles.backtopTop}
      />
    </section>
  );
};

export default About;
